sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    // Ensure room and time_slot are provided before checking
    if (!current.room || !current.slot) {
        return;
    }

    // Initialize a GlideRecord to check for existing appointments with the same room and time slot
    var conflictCheck = new GlideRecord('x_1557275_appoin_0_appointment');
    conflictCheck.addQuery('room', current.room);
    conflictCheck.addQuery('date', current.date);
    conflictCheck.addQuery('slot', current.slot);
    conflictCheck.query();

    // If a conflict is found, prevent the insertion and show an error message
    if (conflictCheck.next()) {
        if (conflictCheck.sys_id == current.sys_id) {
            current.setAbortAction(false);
        }else{
            gs.addErrorMessage('This room is already booked for the selected time slot. Please choose a different time slot.');
            current.setAbortAction(true);
		}
    }

})(current, previous);
