sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    // Add your code here
    // Retrieve the date and slot start/end times
    var appointmentDate = current.date.getDisplayValue();
    var startTime = current.slot.start_time.getDisplayValue(); 
    var endTime = current.slot.end_time.getDisplayValue();

    // Combine date with start and end times and convert to GlideDateTime objects
    var actualStart = new GlideDateTime(appointmentDate + ' ' + startTime);
    var actualEnd = new GlideDateTime(appointmentDate + ' ' + endTime);

    // Set the work_start and work_end fields with the combined date and time
    current.work_start = actualStart;
    current.work_end = actualEnd;

    // Construct the short description
    var customer = current.user.getDisplayValue()
    var slotName = current.slot.name;
    current.short_description = 'Appointment for ' + customer + ' on ' + appointmentDate + ' ' + slotName;
})(current, previous);
