sys_script_include.script:
var ApppointmentAppUtilsClient = Class.create();
ApppointmentAppUtilsClient.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    getAvailableSlots: function(roomSysId, appointmentDate) {
        try {

            var slotSysIds = []; // Arreglo para almacenar los sys_ids de los slots de la habitación
            var slotGR = new GlideRecord('x_1557275_appoin_0_room_time_slot');
            slotGR.addQuery('room', roomSysId);
            slotGR.query();

            while (slotGR.next()) {
                slotSysIds.push(slotGR.getUniqueValue());
            }

            var appointmentSlotSysIds = []; // Arreglo para almacenar los sys_ids de los slots en las citas
            var appointmentGR = new GlideRecord('x_1557275_appoin_0_appointment');
            appointmentGR.addQuery('room', roomSysId);
            appointmentGR.addQuery('date', appointmentDate);
            appointmentGR.query();

            while (appointmentGR.next()) {
                appointmentSlotSysIds.push(appointmentGR.getValue('slot'));
            }

            var availableSlotSysIds = slotSysIds.filter(function(slotId) {
                return appointmentSlotSysIds.indexOf(slotId) === -1; // Incluir solo los slots que no están en las citas
            });

            var encodedQuery = 'sys_idIN' + availableSlotSysIds.join(',');

            return encodedQuery; // Retorna el encoded query de los slots disponibles
        } catch (error) {
            gs.info(error);
        }
    },

    type: 'ApppointmentAppUtilsClient'

});
